def.body:
if web_uri?(context)
  render_web(context)
else
  if asset_uri?(context)
    @static_file_handler.call(context)
  else
    call_next(context)
  end
end

case method
when "get"
  @get = operation
when "put"
  @put = operation
when "post"
  @post = operation
when "delete"
  @delete = operation
when "options"
  @options = operation
when "head"
  @head = operation
when "patch"
  @patch = operation
when "trace"
  @trace = operation
else
  puts("Not implemented")
end
